	public class ACCostSimulationItemsController:BaseBusinessController
	{
		public ACCostSimulationItemsController()
		{
			dal= new DALBaseProvider("ACCostSimulationItems",typeof(ACCostSimulationItemsInfo));
		}
        public ACCostSimulationItemsInfo GetACCostSimulationItemsByProductAndSO(int iARSOID, int iProductID)
        {
            string sQry = string.Format(@" SELECT ACCostSimulationItems.*
                                            FROM dbo.ACCostSimulationItems
                                                INNER JOIN dbo.ACCostSimulations
                                                    ON ACCostSimulations.ACCostSimulationID = ACCostSimulationItems.FK_ACCostSimulationID
                                            WHERE ACCostSimulationItems.AAStatus = 'Alive'
                                                  AND ACCostSimulations.AAStatus = 'Alive'
                                                  AND ACCostSimulations.FK_ARSOID = {0}
                                                  AND ACCostSimulationItems.FK_ICProductID = {1};", iARSOID, iProductID);
            DataSet ds = GetDataSet(sQry);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (ACCostSimulationItemsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            else
                return null;
        }
    }
