    public class ACCostSimulationProductColumnsController : BaseBusinessController
    {
        public ACCostSimulationProductColumnsController()
        {
            dal = new DALBaseProvider("ACCostSimulationProductColumns", typeof(ACCostSimulationProductColumnsInfo));
        }
        public DataSet GetAllDataAndMethodProductByID(int iCostSimulationProductID)
        {
            string strQuery = string.Format(@"SELECT a.*,ACCostSimulationMethodProductNo,ACCostSimulationMethodProductTypeCombo FROM ACCostSimulationProductColumns a
                                JOIN ACCostSimulationMethodProducts b ON a.FK_ACCostSimulationMethodProductID = b.ACCostSimulationMethodProductID
                                    AND a.AAStatus = 'Alive' AND b.AAStatus = 'Alive'
                                WHERE FK_ACCostSimulationProductID = {0}", iCostSimulationProductID);
            return GetDataSet(strQuery);
        }
    }
