        public DataSet GetAllConfigManualByDocTypeAndActionAndFromUser(String strDocType, String strAction)
        {
            String strQuery = String.Format(@"SELECT ADAlertConfigs.*
                                            FROM ADAlertConfigs, ADAlertConfigDocumentTypes, ADDocumentTypes
                                            WHERE ADAlertConfigs.AAStatus = 'Alive'
                                            AND ActiveCheck = 'true'
                                            AND ADDocumentTypes.AAStatus = 'Alive'
                                            AND ADAlertConfigDocumentTypes.FK_ADALertConfigID = ADAlertConfigs.ADAlertConfigID
                                            AND ADAlertConfigDocumentTypes.FK_ADDocumentTypeID = ADDocumentTypes.ADDocumentTypeID
                                            AND ADDocumentTypes.ADDocumentTypeNo = '{0}'
                                            AND ADAlertConfigSchedulerTypeCombo <> 'Scheduler'
                                            AND ADAlertConfigFireOnActionCombo = '{1}'", strDocType, strAction);

            return GetDataSet(strQuery);
        }

        public ADAlertConfigsInfo GetAllConfigManualByDocType(String strDocType)
        {
            String strQuery = String.Format(@"SELECT ADAlertConfigs.*
                                            FROM ADAlertConfigs, ADAlertConfigDocumentTypes, ADDocumentTypes
                                            WHERE ADAlertConfigs.AAStatus = 'Alive'
                                            AND ActiveCheck = 'true'
                                            AND ADDocumentTypes.AAStatus = 'Alive'
                                            AND ADAlertConfigDocumentTypes.FK_ADALertConfigID = ADAlertConfigs.ADAlertConfigID
                                            AND ADAlertConfigDocumentTypes.FK_ADDocumentTypeID = ADDocumentTypes.ADDocumentTypeID
                                            AND ADDocumentTypes.ADDocumentTypeNo = '{0}'", strDocType);
            DataSet ds=GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (ADAlertConfigsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[0]);

            return null;
        }
