	public class APPmtTermItemsController:BaseBusinessController
	{
		public APPmtTermItemsController()
		{
			dal= new DALBaseProvider("APPmtTermItems",typeof(APPmtTermItemsInfo));
		}

        public APPmtTermItemsInfo GetObjectByPmtTermIDAndDayDue(int PmtTermID, int daydue)
        {
            String query = String.Format("Select * From APPmtTermItems Where AAStatus = 'Alive' And FK_APPmtTermID = {0} And APPmtMthTermDueDateRange >= {1} Order By APPmtMthTermDueDateRange", PmtTermID, daydue);
            DataSet ds = dal.GetDataSet(query);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (APPmtTermItemsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);

            return null;
        }
	}
