        public APSupplierKPIItemsInfo GetItemBySupplierKPIAndScore(int iSupplierKPI, double dScore)
        {
            String strQuery = String.Format(@"SELECT * FROM APSUpplierKPIItems WHERE FK_APSupplierKPIID = {0} AND APSupplierKPIFromScore <= {1} AND APSupplierKPIToScore >= {1}", iSupplierKPI, dScore);

            DataSet ds = GetDataSet(strQuery);

            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (APSupplierKPIItemsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[0]);

            return null;
        }
	}
