	public class APLandedCostItemsController:BaseBusinessController
	{
		public APLandedCostItemsController()
		{
			dal= new DALBaseProvider("APLandedCostItems",typeof(APLandedCostItemsInfo));
		}
        public APLandedCostItemsInfo GetLandedCostTotByRcptItmID(int iReceiptItemID, int iExceptLandedCostID = 0)
        {
            string strQuery = String.Format(@"
SELECT ISNULL(SUM(APLandedCostItemAmt),0) APLandedCostItemAmt
	, ISNULL(SUM(APLandedCostItemFAmt),0) APLandedCostItemFAmt
FROM APLandedCostItems a
INNER JOIN APLandedCosts b
	ON b.APLandedCostID = a.FK_APLandedCostID
		AND b.AAStatus = 'Alive'
WHERE FK_ICReceiptItemID = {0}
    {1}", iReceiptItemID, iExceptLandedCostID > 0 ? string.Format(@" AND APLandedCostID <> {0}", iExceptLandedCostID) : "");
            DataSet ds = dal.GetDataSet(strQuery);
            if (ds != null && ds.Tables[0].Rows != null && ds.Tables[0].Rows.Count != 0)
            {
                return (APLandedCostItemsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }
    }
	#endregion
