        //Thang add - 10/12/2015 lay receiptpinvoice by pinvoiceid
        public APPInvoiceReceiptsInfo GetReceiptPInvoiceByPInvoiceID(int iPInvoiceID, int iReceiptID)
        {
            DataSet ds = GetDataSet(string.Format("SELECT * FROM APPInvoiceReceipts WHERE AAStatus = 'Alive' AND FK_APPInvoiceID = {0} AND FK_ICReceiptID = {1}", iPInvoiceID, iReceiptID));
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (APPInvoiceReceiptsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            return null;
        }
	}
