	public class APApprovedPricesController:BaseBusinessController
	{
		public APApprovedPricesController()
		{
			dal= new DALBaseProvider("APApprovedPrices",typeof(APApprovedPricesInfo));
		}

        public DataSet LayTatCaDuLieuConSoLuong(int iAPPRID, String strApproved)
        {
            String str = String.Format(@"SELECT  *
                                        FROM    dbo.APApprovedPrices
                                        WHERE   AAStatus = 'Alive'
                                                AND FK_APPRID = {0}
                                                AND ApprovalStatusCombo = '{1}'
                                                AND APApprovedPriceID IN ( SELECT   FK_APApprovedPriceID
                                                                            FROM     dbo.APPRItemRFQs
                                                                            WHERE    AAStatus = 'Alive'
                                                                                    AND APPRItemRFQRQty > 0 )"
                                    , iAPPRID, strApproved);
            return dal.GetDataSet(str);
        }
	}
	#endregion
