	public class APContractsController:BaseBusinessController
	{
		public APContractsController()
		{
			dal= new DALBaseProvider("APContracts",typeof(APContractsInfo));
		}

        public APContractsInfo GetContract(int iSupplierID, DateTime dt)
        {
            String strDateCondFrom = DALUtil.GennerateCondition("APContractFromDate", CompareType.LessEqualThan, dt);
            String strDateCondTo = DALUtil.GennerateCondition("APContractToDate", CompareType.GreaterEqualThan, dt);

            String strQuery = String.Format(@"SELECT * FROM APContracts
                                                WHERE AAStatus = 'Alive'
                                                AND FK_APSupplierID = {0}
                                                AND {1}
                                                AND {2} ", iSupplierID, strDateCondFrom, strDateCondTo);

            DataSet ds = GetDataSet(strQuery);

            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (APContractsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[0]);

            return null;
        }

        public APContractsInfo GetLastObjectBySupplier(int iSupplierID)
        {
            String strQuery = String.Format(@"SELECT *
                                                FROM
                                                dbo.APContracts WHERE AAStatus='Alive' 
                                                AND FK_APSupplierID={0}
                                                ORDER BY APContractToDate DESC", iSupplierID);
            DataSet ds = GetDataSet(strQuery);

            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (APContractsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[0]);

            return null;
        }
    }
	#endregion
}