	public class APSupplierBanksController:BaseBusinessController
	{
		public APSupplierBanksController()
		{
			dal= new DALBaseProvider("APSupplierBanks",typeof(APSupplierBanksInfo));
		}
        public APSupplierBanksInfo GetDefault(int iSupplierID = 0)
        {
            String strQuery = String.Format(@"SELECT * FROM APSupplierBanks WHERE AAStatus = 'Alive' AND APSupplierBankDefault = 1");

            if (iSupplierID > 0)
                strQuery += string.Format(" AND FK_APSupplierID = {0}", iSupplierID);

            DataSet ds = dal.GetDataSet(strQuery);

            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (APSupplierBanksInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);

            return null;
        }
	}
	#endregion
