	public class APSupplierContactsController:BaseBusinessController
	{
		public APSupplierContactsController()
		{
			dal= new DALBaseProvider("APSupplierContacts",typeof(APSupplierContactsInfo));
		}

        //Thanh Trung adds, 08/05/2008
        public List<APSupplierContactsInfo> GetAPSupplierContactsListByAPSupplierIDAndAPSupplierContactType(int iAPSupplierID, String strAPSupplierContactType)
        {
            List<APSupplierContactsInfo> lstAPSupplierContactsInfo = new List<APSupplierContactsInfo>();

            DataSet dsAPSupplierContacts = GetAllDataByForeignColumn("FK_APSupplierID", iAPSupplierID);
            if (dsAPSupplierContacts.Tables.Count > 0)
            {
                foreach (DataRow rowAPSupplierContact in dsAPSupplierContacts.Tables[0].Rows)
                {
                    APSupplierContactsInfo objAPSupplierContactsInfo = (APSupplierContactsInfo)GetObjectFromDataRow(rowAPSupplierContact);
                    if (objAPSupplierContactsInfo.APContactType.Contains(strAPSupplierContactType))
                        lstAPSupplierContactsInfo.Add(objAPSupplierContactsInfo);
                }
            }

            return lstAPSupplierContactsInfo;
        }
	}
	#endregion
