	public class ARShpPlanItemDetailsController:BaseBusinessController
	{
		public ARShpPlanItemDetailsController()
		{
			dal= new DALBaseProvider("ARShpPlanItemDetails",typeof(ARShpPlanItemDetailsInfo));
		}
        public double GetAllocateQtyByProductInRange(int iProductID, int iFromPeriod, int iFromYear, int iToPeriod, int iToYear)
        {
            string strQuery = string.Format(@"Select SUM(ARShpPlanItemDetailAllocateQty) from ARShpPlanItemDetails where FK_ICProductID={0} AND AAStatus='Alive'
                                                   AND FK_ARShpPlanItemID in (select ARShpPlanItemID from ARShpPlanItems where AAStatus='Alive')
                                                   AND FK_ARShpPlanID in (select ARShpPlanID from ARShpPlans where AAStatus='Alive' AND
                                                           ARShpPlanPeriod+ARShpPlanYear*12>={1} AND ARShpPlanPeriod+ARShpPlanYear*12<={2})"
                                    , iProductID, iFromPeriod + iFromYear * 12, iToPeriod + iToYear * 12);
            DataSet ds = dal.GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0 && ds.Tables[0].Rows[0][0] != DBNull.Value)
                return Convert.ToDouble(ds.Tables[0].Rows[0][0]);
            return 0;
        }
	}
	#endregion
