        public DataSet LayARCustomerIDByID(int iARCustomerID, String strFK_ICProductID)
        {
            String strQuery = String.Format(@"SELECT *FROM ARCustomerProductReqItems 
                                                WHERE AAStatus = 'Alive' 
                                                AND ( FK_ICProductID IS NULL {1})
                                                AND fk_ARcustomerreqID IN (
								                                            SELECT ARCustomerReqid FROM dbo.ARCustomerReqs 
								                                            WHERE aastatus = 'Alive' 
                                                                            AND FK_ARCustomerID IN (SELECT ARCustomerID FROM dbo.ARCustomers 
															                WHERE AAStatus = 'Alive' AND ARCustomerID = {0} ))", iARCustomerID, strFK_ICProductID);
            return GetDataSet(strQuery);
