	public class ARCustomerReqAttrItemsController:BaseBusinessController
	{
		public ARCustomerReqAttrItemsController()
		{
			dal= new DALBaseProvider("ARCustomerReqAttrItems",typeof(ARCustomerReqAttrItemsInfo));
        }

        #region Kiểm tra ARCustomerReqAttrItemNo có tồn tại trong bộ cha của nó chưa --Vinh

        public Boolean CheckARCustomerReqAttrItemNoExist(int iARCustomerReqAttrID, String strARCustomerReqAttrItemNo)
        {
            String str = String.Format(@"   SELECT * FROM dbo.ARCustomerReqAttrItems
                                            WHERE AAStatus = 'Alive'
                                            AND ARCustomerReqAttrItemNo LIKE N'{0}'
                                            AND FK_ARCustomerReqAttrID IN (
								                                            SELECT ARCustomerReqAttrID FROM dbo.ARCustomerReqAttrs
								                                            WHERE AAStatus = 'Alive'
								                                            AND ARCustomerReqAttrID = {1}
								                                            )"
                                        , strARCustomerReqAttrItemNo, iARCustomerReqAttrID);

            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return true;
            }

            return false;
        }

        #endregion
    }
