        #region Lấy ARCustomerReqNo -- Tài -- 13/04/2015
        public DataSet LayARCustomerIDByID(int iARCustomerNo)
        {
            String strQuery = String.Format(@"SELECT *
FROM arcustomerproductreqs 
WHERE aastatus = 'Alive'
      AND fk_ARcustomerreqID IN (
								SELECT ARCustomerReqid
								FROM dbo.ARCustomerReqs 
								WHERE AAStatus = 'Alive' and FK_ARCustomerID IN (SELECT ARCustomerID 
															FROM dbo.ARCustomers 
															WHERE ARCustomerID = {0}))", iARCustomerNo);
            return GetDataSet(strQuery);
        }
        #endregion
    }
