        #region Lấy tât cả thuộc tính theo Routing và sản phẩm --Vinh

        public DataSet GetAllDataByQuotANDProduct(int iARQuotID, int iICProductID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.ARQuotItemAttrs
                                            WHERE AAStatus = 'Alive'
                                            AND FK_ARQuotID = {0}
                                            AND FK_ICProductID = {1}
                                            ", iARQuotID, iICProductID);
            return dal.GetDataSet(str);
        }

