	public class ARSellersController:BaseBusinessController
	{
		public ARSellersController()
		{
			dal= new DALBaseProvider("ARSellers",typeof(ARSellersInfo));
		}

        public DataSet GetAllSellerByID(int iSellerID)
        {
            String strIDCond = String.Empty;
            if (iSellerID > 0)
                strIDCond = String.Format(@" AND ARSellerID = {0}", iSellerID);

            String strQuery = String.Format(@"SELECT * FROM ARSellers WHERE AAStatus = 'Alive' {0}", strIDCond);

            return GetDataSet(strQuery);
        }
        public DataSet GetAllSellerManager()
        {
            String strQuery = String.Format(@"  SELECT ARSellerID,
                                                        ARSellerNo,
                                                        ARSellerName
                                                FROM dbo.ARSellers
                                                WHERE AAStatus = 'Alive'
                                                        AND ARSellerID IN
                                                            (
                                                                SELECT DISTINCT
                                                                    FK_ARSellerManagerID
                                                                FROM dbo.ARSellers
                                                                WHERE AAStatus = 'Alive'
                                                            );
                                                ");
            DataSet ds = GetDataSet(strQuery);
            return ds;
        }
    }
	#endregion
}