	public class CMAccountsController:BaseBusinessController
	{
		public CMAccountsController()
		{
			dal= new DALBaseProvider("CMAccounts",typeof(CMAccountsInfo));
		}

        public List<BusinessObject> GetObjectsLikeNo(string AccountNo)
        {
            string strQuery = String.Format("SELECT * FROM CMAccounts WHERE AAStatus='Alive' AND CMAccountNo LIKE '%{0}%'", AccountNo);
            DataSet ds = GetDataSet(strQuery);
            List<BusinessObject> list = GetListFromDataset(ds);
            return list;
        }
    }
