	public class CMContactsController:BaseBusinessController
	{
		public CMContactsController()
		{
			dal= new DALBaseProvider("CMContacts",typeof(CMContactsInfo));
		}

        public int GetCountContactByCampaignID(int iCampaignID)
        {
            string strQuery = String.Format(@"SELECT COUNT(*) FROM CMContacts WHERE AAStatus='Alive' AND FK_CMCampaignID={0}", iCampaignID);
            DataSet ds = (DataSet)GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0 && ds.Tables[0].Rows[0][0] != DBNull.Value)
                return Convert.ToInt32(ds.Tables[0].Rows[0][0]);

            return 0;
        }

        public List<BusinessObject> GetObjectsLikeNo(string ContactNo)
        {
            string strQuery=String.Format("SELECT * FROM CMContacts WHERE AAStatus='Alive' AND CMContactNo LIKE '%{0}%'",ContactNo);
            DataSet ds = GetDataSet(strQuery);
            List<BusinessObject> list = GetListFromDataset(ds);
            return list;
        }
    }
