	public class FAAssetActionHistorysController:BaseBusinessController
	{
		public FAAssetActionHistorysController()
		{
			dal= new DALBaseProvider("FAAssetActionHistorys",typeof(FAAssetActionHistorysInfo));
        }

        #region Lấy lịch sử của gần nhất của chiến dịch.

        public FAAssetActionHistorysInfo GetLastHistory(int iFAAssetID)
        {
            String str = String.Format(@"   SELECT TOP 1 * FROM dbo.FAAssetActionHistorys
                                            WHERE FK_FAAssetID = {0}
                                            ORDER BY FAAssetActionHistoryID DESC ", iFAAssetID);
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (FAAssetActionHistorysInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }

        #endregion
    }
