	public class FAAssetCriteriaItemsController:BaseBusinessController
	{
		public FAAssetCriteriaItemsController()
		{
			dal= new DALBaseProvider("FAAssetCriteriaItems",typeof(FAAssetCriteriaItemsInfo));
		}
        public bool IsValidDepartmentPct(int iFAAssetCriteriaID)
        {
            string strQuery = string.Format(@"
SELECT 1
    FROM FAAssetCriteriaItems 
    WHERE AAStatus='Alive' AND FK_FAAssetCriteriaID={0}
    HAVING SUM(FAAssetDepartmentPct) = 100
", iFAAssetCriteriaID);
            DataSet ds = dal.GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return true;
            return false;
        }
        
	}
