	public class GENumberingsController:BaseBusinessController
	{
		public GENumberingsController()
		{
			dal= new DALBaseProvider("GENumberings",typeof(GENumberingsInfo));
        }

        #region Lấy GENumberings theo module name --Vinh

        public GENumberingsInfo GetObjectByModuleName(String strModuleName)
        {
            String str = String.Format(@"   SELECT * FROM dbo.GENumberings
                                            WHERE GENumberingModuleName LIKE '{0}'"
                                        , strModuleName);

            DataSet ds = dal.GetDataSet(str);
            if(ds!=null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (GENumberingsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }

        #endregion

        #region Lấy tất cả module Name, Module Desc được sử dụng --Vinh

        public DataSet GetModuleNameANDDesc()
        {
            String str = String.Format(@"SELECT STModuleName, STModuleDescriptionDescription FROM dbo.CSCompanyModules
                                        INNER JOIN dbo.STModules ON STModules.STModuleID = CSCompanyModules.FK_STModuleID
                                        INNER JOIN dbo.STModuleDescriptions ON STModuleDescriptions.STModuleID = STModules.STModuleID");

            return dal.GetDataSet(str);
        }

        #endregion

        #region Lấy số tự động hiện tại lớn nhất trong module --Vinh

        public int GetAutoNumberMax(String strTableName, String strColumnNo, String strObjectNoRef, int iAutoNumber)
        {
            if (iAutoNumber == 0) return 0;
            String str = String.Format(@"   SELECT MAX(RIGHT({1}, {3})) FROM dbo.{0}
                                            WHERE AAStatus = 'Alive'
                                            AND {1} LIKE '{2}%'"
                                        , strTableName, strColumnNo, strObjectNoRef, iAutoNumber);

            DataSet dsNumberMax = dal.GetDataSet(str);
            if(dsNumberMax!=null && dsNumberMax.Tables.Count > 0 && dsNumberMax.Tables[0].Rows.Count >0 && dsNumberMax.Tables[0].Rows[0][0] != DBNull.Value)
            {
                try
                {
                    return Convert.ToInt32(dsNumberMax.Tables[0].Rows[0][0]);
                }
                catch { }
            }

            return 0;
        }
        #endregion

        #region Lấy tất cả dữ liệu theo Loại --Vinh

        public  DataSet GetAllDataByObjectType(String strObjectType)
        {
            String str = String.Format(@"SELECT a.*,c.STModuleDescriptionDescription FROM dbo.GENumberings a INNER JOIN STModules b ON a.GENumberingName = b.STModuleName INNER JOIN STModuleDescriptions c ON b.STModuleID = c.STModuleID
                                        WHERE (GENumberingObjectType LIKE '{0}' or '{0}'='')
                                        ", strObjectType);
            return dal.GetDataSet(str);
        }

        #endregion
    }
	#endregion
