        public ARAccountSetsInfo GetDefault()
        {
            String strQuery = String.Format(@"SELECT * FROM ARAccountSets WHERE AADefault = 'true'");

            DataSet ds = dal.GetDataSet(strQuery);

            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
