	public class GLBudgetPeriodsController:BaseBusinessController
	{
		public GLBudgetPeriodsController()
		{
			dal= new DALBaseProvider("GLBudgetPeriods",typeof(GLBudgetPeriodsInfo));
		}
        public GLBudgetPeriodsInfo GetBudgetPeriodbyYear(int Period)
        {
            String strQuery = String.Format(@"SELECT * FROM GLBudgetPeriods WHERE AAStatus='Alive' AND GLBudgetPeriodPeriod={0} ", Period);
            DataSet dss = dal.GetDataSet(strQuery);
            if (dss.Tables.Count <= 0 || dss.Tables[0].Rows.Count <= 0) return null;
            return (GLBudgetPeriodsInfo)dal.GetObjectFromDataRow(dss.Tables[0].Rows[0]);
        }
	}
	#endregion
