	public class GLBudgetsController:BaseBusinessController
	{
		public GLBudgetsController()
		{
			dal= new DALBaseProvider("GLBudgets",typeof(GLBudgetsInfo));
		}

        public GLBudgetsInfo GetBudgetbyYearAndAccount(int year,int AccountID)
        {
            String strQuery = String.Format(@"SELECT * FROM GLBudgets WHERE AAStatus='Alive' AND GLBudgetYear={0} AND FK_GLAccountID={1}",year,AccountID);
            DataSet dss = dal.GetDataSet(strQuery);
            if (dss.Tables.Count <= 0 || dss.Tables[0].Rows.Count <= 0) return null;
            return (GLBudgetsInfo)dal.GetObjectFromDataRow( dss.Tables[0].Rows[0]);
        }
	}
	#endregion
}