	public class GLPeriodHistorysController:BaseBusinessController
	{
		public GLPeriodHistorysController()
		{
			dal= new DALBaseProvider("GLPeriodHistorys",typeof(GLPeriodHistorysInfo));
		}

        public DataSet LayLichSuKhoaSoTheoThoiGian(int iYear, int iMonth)
        {
            String str = String.Format(@"   SELECT * FROM dbo.GLPeriodHistorys
                                            WHERE AAStatus = 'Alive'
                                            AND GLPeriodHistoryYear = {0}
                                            AND (GLPeriodHistoryMonth = {1} OR {1} = 0)"
                                        , iYear, iMonth);
            return dal.GetDataSet(str);
        }
	}
