	public class GLTranCfgUsersController:BaseBusinessController
	{
		public GLTranCfgUsersController()
		{
			dal= new DALBaseProvider("GLTranCfgUsers",typeof(GLTranCfgUsersInfo));
		}
        public DataSet GetDataGroupUser(string psNotINID = "")
        {
            String strQuery = String.Format(@"SELECT  b.* ,
        b.ADUserID AS FK_ADUserID,a.ADUserGroupName
FROM    ADUserGroups a
        INNER JOIN ADUsers b ON a.AAStatus = 'Alive'
                                AND b.AAStatus = 'Alive'
                                AND a.ADUserGroupID = b.ADUserGroupID");
            if (psNotINID != "")
                strQuery += String.Format(@"
                                AND b.ADUserID NOT IN ({0})", psNotINID);
            return dal.GetDataSet(strQuery);
        }
	}
