	public class HRBonusAllowancesController:BaseBusinessController
    {
		public HRBonusAllowancesController()
		{
			dal= new DALBaseProvider("HRBonusAllowances",typeof(HRBonusAllowancesInfo));
        }

        public DataSet GetAllDataByDate(DateTime dtFrom, DateTime dtTo)
        {
            String str = String.Format(@"   SELECT * FROM dbo.HRBonusAllowances
                                            WHERE AAStatus = 'Alive'
                                            AND (HRBonusAllowanceMonth + HRBonusAllowanceYear * 12) >= {0}
                                            AND (HRBonusAllowanceMonth + HRBonusAllowanceYear * 12) <= {1}", dtFrom.Month + dtFrom.Year * 12, dtTo.Month + dtTo.Year * 12);
            return dal.GetDataSet(str);
        }

    }
	#endregion
