        #region Lấy tất bằng cấp theo loại và theo hồ sơ -- Vinh --28/05/2013

        public DataSet GetAllCandidateDegreeByCandidateANDType(int iCandidateID, String strTypeCombo)
        {
            String str = String.Format(@"   SELECT * FROM dbo.HRCandidateDegrees
                                            WHERE AAStatus='Alive'
                                            AND FK_HRCandidateID = {0}
                                            AND HRCandidateDegreeTypeCombo LIKE '{1}' ", iCandidateID, strTypeCombo);
            return dal.GetDataSet(str);
        }
        
