	public class HRContractsController:BaseBusinessController
	{
		public HRContractsController()
		{
			dal= new DALBaseProvider("HRContracts",typeof(HRContractsInfo));
		}

        #region Lấy hợp đồng mẫu Default --Vinh --20/03/2013

        public HRContractsInfo GetContractByDefault()
        {
            String str = String.Format(@"   SELECT * FROM HRContracts WHERE AAStatus='Alive'
                                            AND HRContractDefaultCheck = 1
                                            ");

            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (HRContractsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }

        #endregion
	}
	#endregion
