	public class HRDiligentCfgsController:BaseBusinessController
	{
		public HRDiligentCfgsController()
		{
			dal= new DALBaseProvider("HRDiligentCfgs",typeof(HRDiligentCfgsInfo));
		}

        #region Kiểm tra các điều kiện chuyên cần đã có tồn tại chưa, True nếu có, False nếu không có --Vinh 28/03/2013

        public Boolean CheckExistHRDiligentCfgs(double dbLeaveCnt, double dbUnauthorizedLeaveCnt, double dbAbsenceOTCnt)
        {
            String str = String.Format(@"   SELECT * FROM dbo.HRDiligentCfgs 
                                            WHERE AAStatus='Alive'
                                            AND HRDiligentCfgLeaveCnt = {0}
                                            AND HRDiligentCfgUnauthorizedLeaveCnt = {1}
                                            AND HRDiligentCfgAbsenceOTCnt = {2}", dbLeaveCnt, dbUnauthorizedLeaveCnt, dbAbsenceOTCnt);
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return true;
            }
            return false;
        }

        #endregion
    }
	#endregion
