	public class HREmployeeContractAllowancesController:BaseBusinessController
	{
		public HREmployeeContractAllowancesController()
		{
			dal= new DALBaseProvider("HREmployeeContractAllowances",typeof(HREmployeeContractAllowancesInfo));
        }

        #region Lấy giá trị phụ cấp trong hợp đồng theo loại --Vinh --19/03/2013

        public double GetAmtEmployeeContractAllowance(int iEmployeeContractID, String strTypeCombo)
        {
            String str = String.Format(@"   SELECT SUM(HREmployeeContractAllowanceAmt) FROM dbo.HREmployeeContractAllowances
                                            WHERE  AAStatus='Alive'
                                            AND HRBonusAllowanceTypeCombo LIKE '{0}'
                                            AND FK_HREmployeeContractID={1}",strTypeCombo, iEmployeeContractID);
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count>0 && ds.Tables[0].Rows[0][0] != DBNull.Value)
            {
                return double.Parse(ds.Tables[0].Rows[0][0].ToString());
            }

            return 0;
        }

        #endregion
    }
	#endregion
