        public HREmployeeLeaveTypesInfo GetEmployeeLeaveTypeByTypeNameCombo(String strTypeNameCombo)
        {
            String str = String.Format(@"SELECT * FROM dbo.HREmployeeLeaveTypes WHERE AAStatus='Alive' AND HREmployeeLeaveTypeCombo LIKE '%{0}%'", strTypeNameCombo);
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (HREmployeeLeaveTypesInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }
