        public HREmployeeSchedulingDetailsInfo GetDataByEmployeeANDDayOfWeekANDShift(int iEmployeeID, int iShiftID, DayOfWeek dayofweek)
        {
            String str = String.Format(@"   SELECT * FROM dbo.HREmployeeSchedulingDetails
                                            WHERE FK_HRShiftID = {0}
                                            AND FK_HREmployeeSchedulingID IN (
									                                            SELECT HREmployeeSchedulingID FROM dbo.HREmployeeSchedulings
									                                            WHERE AAStatus = 'Alive'
									                                            AND FK_HREmployeeID = {1}
									                                            AND DATENAME(dw,HREmployeeSchedulingDayOfWeek) = '{2}'
									                                            )", iShiftID, iEmployeeID, dayofweek.ToString());
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (HREmployeeSchedulingDetailsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }

        public DataSet GetDataByEmployeeANDDayOfWeek(int iEmployeeID, DayOfWeek dayofweek)
