									                                            SELECT HREmployeeSchedulingID FROM dbo.HREmployeeSchedulings
									                                            WHERE AAStatus = 'Alive'
									                                            AND FK_HREmployeeID = {0}
									                                            AND DATENAME(dw,HREmployeeSchedulingDayOfWeek) = '{1}'
