    public class HRFundsController : BaseBusinessController
    {
        public HRFundsController()
        {
            dal = new DALBaseProvider("HRFunds",typeof(HRFundsInfo));
        }
       public DataSet getAllFundAmtByYear(int iyear)
        {
            string str = string.Format(@"SELECT FK_HREmployeeID, SUM(HRFundItemAmt) HRFundItemAmt
                                        FROM dbo.HRFundItems
                                        JOIN dbo.HRFunds ON (HRFunds.HRFundID = HRFundItems.FK_HRFundID)
                                        WHERE AAStatus='Alive' AND HRFundYear={0}
                                        GROUP BY FK_HREmployeeID", iyear);
          return  dal.GetDataSet(str);
        }
    }
