        #region Copy From PS --Vinh --10/01/2013

        public Boolean CheckDateIsHoliday(DateTime dTime)
        {
            String str = String.Format(@"SELECT * FROM dbo.HRHolidays WHERE AAStatus='Alive' AND CONVERT(NVARCHAR(10),HRHolidayDate,112)=CONVERT(NVARCHAR(10),'{0}',112)", dTime.ToString("yyyyMMdd"));
            DataSet ds = dal.GetDataSet(str);
            if (ds == null || ds.Tables.Count <= 0 || ds.Tables[0].Rows.Count <= 0)//ko ton tai 
                return false;
            else
                return true;
        }

        public HRHolidaysInfo GetHoliday(DateTime dTime)
        {
            String str = String.Format(@"SELECT * FROM dbo.HRHolidays WHERE AAStatus='Alive' AND CONVERT(NVARCHAR(10),HRHolidayDate,112)=CONVERT(NVARCHAR(10),'{0}',112)", dTime.ToString("yyyyMMdd"));
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (HRHolidaysInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }
        //hieu nguyen Add,delete all Holiday by year
        public void DeleteHolidayByCurrentYear(int iCurrentYear)
        {
            String strQuery = String.Format("Delete [dbo].[HRHolidays] WHERE [HRHolidayFiscalYear]={0}", iCurrentYear);
            DataSet ds = dal.GetDataSet(strQuery);
        }
        //hieu nguyen add,
        public DataSet GetHolidayByYear(int iYear)
        {
            String strQuery = String.Format("SELECT * FROM [dbo].[HRHolidays] WHERE [HRHolidayFiscalYear] ={0} AND [AAStatus]='{1}'", iYear, BusinessObject.DefaultAAStatus);
            return dal.GetDataSet(strQuery);
        }
        public DataSet GetHolidayByFDdateTDate(DateTime dtFdate, DateTime dtTdate)
        {
            String str = String.Format(@"SELECT * FROM dbo.HRHolidays
                                         WHERE AAStatus='Alive' 
                                         AND CONVERT(NVARCHAR(10),HRHolidayDate,112)>=CONVERT(NVARCHAR(10),'{0}',112)
                                         AND CONVERT(NVARCHAR(10),HRHolidayDate,112)<=CONVERT(NVARCHAR(10),'{1}',112)", dtFdate.ToString("yyyyMMdd"), dtTdate.ToString("yyyyMMdd"));
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return ds;
            }
            return null;
        }
