	public class HRPeriodsController:BaseBusinessController
	{
		public HRPeriodsController()
		{
			dal= new DALBaseProvider("HRPeriods",typeof(HRPeriodsInfo));
		}
        public void DeleteAllDataByYear(int iyear)
        {
            string str = string.Format(@"DELETE FROM HRPeriods
                                        WHERE HRPeriodYear = {0}", iyear);
            dal.GetDataSet(str);
        }
        public HRPeriodsInfo GetOjectByDate (DateTime dtDate)
        {
            string str = string.Format(@"SELECT * FROM HRPeriods
                                         WHERE AAStatus='Alive'
                                         AND CONVERT(NVARCHAR(10),HRPeriodFromDate,112) <= '{0}'
                                         AND CONVERT(NVARCHAR(10),HRPeriodToDate,112) >='{0}'", dtDate.ToString("yyyyMMdd"));
           DataSet ds=dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (HRPeriodsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            else
                return null;
        }
        public HRPeriodsInfo GetOjectByFDateAndTDate(DateTime dtFDate, DateTime dtTDate)
        {
            string str = string.Format(@"SELECT * FROM HRPeriods
                                         WHERE AAStatus='Alive'
                                         AND CONVERT(NVARCHAR(10),HRPeriodFromDate,112) = '{0}'
                                         AND CONVERT(NVARCHAR(10),HRPeriodToDate,112) ='{1}'", dtFDate.ToString("yyyyMMdd"), dtTDate.ToString("yyyyMMdd"));
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (HRPeriodsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            else
                return null;
        }
    }
