	public class HRRecruitReqItemsController:BaseBusinessController
	{
		public HRRecruitReqItemsController()
		{
			dal= new DALBaseProvider("HRRecruitReqItems",typeof(HRRecruitReqItemsInfo));
		}
        public DataSet GetAllDataAppAndNonePln()
        {
            string strQuery = string.Format(@"SELECT * 
                                                FROM dbo.HRRecruitReqItems
                                                JOIN dbo.HRRecruitReqs 
                                                ON (HRRecruitReqs.HRRecruitReqID = HRRecruitReqItems.FK_HRRecruitReqID AND HRRecruitReqs.AAStatus = HRRecruitReqItems.AAStatus)
                                                WHERE HRRecruitReqItems.AAStatus='Alive' AND ApprovalStatusCombo='Approved'
                                                AND HRRecruitReqItemPlnDate IS NULL");
            return dal.GetDataSet(strQuery);
        }

        public DataSet GetAllDataReqPln()
        {
            string strQuery = string.Format(@"SELECT * 
                                                FROM dbo.HRRecruitReqItems
                                                JOIN dbo.HRRecruitReqs 
                                                ON (HRRecruitReqs.HRRecruitReqID = HRRecruitReqItems.FK_HRRecruitReqID AND HRRecruitReqs.AAStatus = HRRecruitReqItems.AAStatus)
                                                WHERE HRRecruitReqItems.AAStatus='Alive' AND ApprovalStatusCombo='Approved'
                                                AND HRRecruitReqItemPlnDate IS NOT NULL");
            return dal.GetDataSet(strQuery);
        }
    }
