	public class HRRegionsController:BaseBusinessController
	{
		public HRRegionsController()
		{
			dal= new DALBaseProvider("HRRegions",typeof(HRRegionsInfo));
		}
        public DataSet GetAlldataRegion()
        {
            string strQuery = string.Format("Select * from HRRegions where AAStatus='Alive' And FK_HRMainRegionID=0");
            return dal.GetDataSet(strQuery);

        }
        public DataSet GetAlldataRegionChild()
        {
            string strQuery = string.Format("Select * from HRRegions where AAStatus='Alive' And FK_HRMainRegionID>0");
            return dal.GetDataSet(strQuery);

        }
        public DataSet GetAlldataRegionChildByMainRegionID(int iRegionID)
        {
            string strQuery = string.Format("Select * from HRRegions where AAStatus='Alive' And FK_HRMainRegionID>0 and FK_HRMainRegionID={0}", iRegionID);
            return dal.GetDataSet(strQuery);

        }

    }
