	public class HRSalStructItemsController:BaseBusinessController
	{
		public HRSalStructItemsController()
		{
			dal= new DALBaseProvider("HRSalStructItems",typeof(HRSalStructItemsInfo));
		}
       public DataSet GetAllParamBySalStructTransactionID(int iSalStructTransactionID)
        {
            string str = string.Format(@"SELECT DISTINCT  HRSalStructItemParam as HRSalStructItemParam
                                        FROM dbo.HRSalStructItems
                                        JOIN dbo.HRSalStructs ON(HRSalStructs.HRSalStructID = HRSalStructItems.FK_HRSalStructID
                                        AND HRSalStructs.AAStatus = HRSalStructItems.AAStatus)
                                        WHERE HRSalStructItems.AAStatus = 'Alive' AND FK_HRSalStructTransactionID ={0}
            ", iSalStructTransactionID);
            return dal.GetDataSet(str);
        }

    }
