	public class HRSalStructObjectsController:BaseBusinessController
	{
		public HRSalStructObjectsController()
		{
			dal= new DALBaseProvider("HRSalStructObjects",typeof(HRSalStructObjectsInfo));
		}
        public DataSet GetAllDataBySalStructTransactionID(int iSalStructTransactionID)
        {
            string str = string.Format(@"SELECT  *
                                        FROM    dbo.HRObjects
                                                JOIN ( SELECT DISTINCT
                                                                FK_HRObjectID
                                                       FROM     dbo.HRSalStructObjects
                                                                JOIN dbo.HRSalStructs ON ( HRSalStructs.HRSalStructID = HRSalStructObjects.FK_HRSalStructID
                                                                                           AND HRSalStructs.AAStatus = HRSalStructObjects.AAStatus
                                                                                         )
                                                       WHERE    HRSalStructObjects.AAStatus = 'Alive'
                                                                AND FK_HRSalStructTransactionID = {0}
                                                     ) temp ON ( HRObjectID = temp.FK_HRObjectID )
                                        WHERE   AAStatus = 'Alive'", iSalStructTransactionID);
            return dal.GetDataSet(str);
        }
    }
	#endregion
}