        public DataSet LayTatCaNhanVienTheoKhoaHocVaMonHoc(int iTrainingID, int iCourseID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.HRTrainingSemesterCourseEmployees
                                            WHERE AAStatus = 'Alive'
                                            AND (FK_HRTrainingID = {0} OR {0} = 0)
                                            AND (FK_HRCourseID = {1} OR {1} = 0)
                                            AND FK_HRTrainingSemesterCourseID IN	(
											                                            SELECT HRTrainingSemesterCourseID FROM dbo.HRTrainingSemesterCourses
											                                            WHERE AAStatus='Alive'
											                                            AND (FK_HRTrainingID = {0} OR {0} = 0)
											                                            AND (FK_HRCourseID = {1} OR {1} = 0)
										                                            )
                                            AND FK_HRTrainingSemesterEmployeeID IN (
											                                            SELECT HRTrainingSemesterEmployeeID FROM dbo.HRTrainingSemesterEmployees
											                                            WHERE AAStatus='Alive'
											                                            AND (FK_HRTrainingID = {0} OR {0} = 0)
											                                            AND (FK_HRCourseID = {1} OR {1} = 0)
										                                            )", iTrainingID, iCourseID);
            return dal.GetDataSet(str);
        }
