			dal= new DALBaseProvider("ICProductColorModels",typeof(ICProductColorModelsInfo));
		}
        public DataSet GetAllDataByProductColorOrderByPriority(int iProductColorID)
        {
            string strQuery = string.Format(@"Select * from ICProductColorModels where AAStatus='Alive' AND FK_ICProductColorSchemaID={0}
                                                        order by ICProductColorModelPriority"
                                            , iProductColorID);
            return dal.GetDataSet(strQuery);
        }
