        public DataSet getAllWeightUOM()
        {
            string strQuery = string.Format(@"SELECT  *
                                FROM    dbo.ICUOMs
                                WHERE   dbo.ICUOMs.ICUOMID IN (
                                        ( SELECT    FK_ICFromUOMID
                                          FROM      dbo.ICUOMFactors
                                          WHERE     FK_ICFromUOMID = ( SELECT   FK_ICWeightUOMID
                                                                       FROM     dbo.ICSetups
                                                                       WHERE    AAStatus = 'Alive'
                                                                     )
                                                    OR FK_ICToUOMID = ( SELECT  FK_ICWeightUOMID
                                                                        FROM    dbo.ICSetups
                                                                        WHERE   AAStatus = 'Alive'
                                                                      )
                                        )
                                        UNION
                                        ( SELECT    FK_ICToUOMID
                                          FROM      dbo.ICUOMFactors
                                          WHERE     FK_ICFromUOMID = ( SELECT   FK_ICWeightUOMID
                                                                       FROM     dbo.ICSetups
                                                                       WHERE    AAStatus = 'Alive'
                                                                     )
                                                    OR FK_ICToUOMID = ( SELECT  FK_ICWeightUOMID
                                                                        FROM    dbo.ICSetups
                                                                        WHERE   AAStatus = 'Alive'
                                                                      )
                                        ) )
                                        AND dbo.ICUOMs.AAStatus = 'Alive'");

            return dal.GetDataSet(strQuery);
        }

