	public class ICProductColorSchemasController:BaseBusinessController
	{
		public ICProductColorSchemasController()
		{
			dal= new DALBaseProvider("ICProductColorSchemas",typeof(ICProductColorSchemasInfo));
		}
        public DataSet GetAllDataRcpInProductionOrdrByLotNo(int iProductionOrdrID, string strLotNo)
        {
            string strQuery = string.Format(@"Select distinct * from ICProductColorSchemas where AAStatus='Alive' AND
                                           ICProductColorSchemaID in (Select FK_ICProductColorSchemaID from ICProducts
                                        where AAStatus='Alive' AND ICProductID in (Select FK_ICProductID from ICReceiptItems
                                                where AAStatus='Alive' AND FK_PPProductionOrdrID={0} AND ICReceiptItemLotNo='{1}'))", iProductionOrdrID, strLotNo);
            return dal.GetDataSet(strQuery);
        }
        public ICProductColorSchemasInfo GetColorSchemaNormalInProductionOrdrByLotNo(int iProductionOrdrID, string strLotNo)
        {
            string strQuery = string.Format(@"Select Top 1 * from ICProductColorSchemas where AAStatus='Alive' AND
                                           ICProductColorSchemaID in (Select FK_ICProductColorSchemaID from ICProducts
                                        where AAStatus='Alive' AND ICProductID in (Select FK_ICProductID from ICReceiptItems
                                                where AAStatus='Alive' AND FK_PPProductionOrdrID={0} AND ICReceiptItemLotNo='{1}' AND ICReceiptItemProductionTypeCombo='Normal'))", iProductionOrdrID, strLotNo);
            DataSet ds = dal.GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (ICProductColorSchemasInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            return null;
        }
	}
