	public class ICProductTypesController:BaseBusinessController
	{
		public ICProductTypesController()
		{
			dal= new DALBaseProvider("ICProductTypes",typeof(ICProductTypesInfo));
		}
        public int GetObjectIDLikeNo(string strObjectNo)
        {
            DataSet ds = GetDataSet(string.Format("SELECT * FROM {0} WHERE AAStatus = 'Alive' AND ICProductTypeNo LIKE '%{1}%'", dal.TableName, strObjectNo));
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return Convert.ToInt32(ds.Tables[0].Rows[0]["ICProductTypeID"]);
            return 0;
        }
	}
