	public class ICRequestReceiptsController:BaseBusinessController
	{
		public ICRequestReceiptsController()
		{
			dal= new DALBaseProvider("ICRequestReceipts",typeof(ICRequestReceiptsInfo));
		}
        public DataSet GetRequestReceipt(DateTime FromDate, DateTime ToDate)
        {
            String strQuery = String.Format(@"SELECT * FROM dbo.ICRequestReceipts WHERE AAStatus='Alive' 
                                            AND CONVERT(VARCHAR(10), [ICRequestReceiptDate], 112)>= CONVERT(VARCHAR(10), '{0}', 112) 
                                            AND CONVERT(VARCHAR(10), [ICRequestReceiptDate], 112)<= CONVERT(VARCHAR(10), '{1}', 112)", FromDate.ToString("yyyyMMdd"), ToDate.ToString("yyyyMMdd"));
            return dal.GetDataSet(strQuery);
        }      
	}
	#endregion
