	public class ICShipmentItemPurchaseReturnItemsController:BaseBusinessController
	{
		public ICShipmentItemPurchaseReturnItemsController()
		{
			dal= new DALBaseProvider("ICShipmentItemPurchaseReturnItems",typeof(ICShipmentItemPurchaseReturnItemsInfo));
		}

        public DataSet GetAllDataByPurchaseReturn(int iAPPurchaseReturnID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.ICShipmentItemPurchaseReturnItems
                                            WHERE AAStatus = 'Alive'
                                            AND FK_APPurchaseReturnItemID IN (
									                                            SELECT APPurchaseReturnItemID FROM dbo.APPurchaseReturnItems
									                                            WHERE AAStatus = 'Alive'
									                                            AND FK_APPurchaseReturnID = {0}
								                                             )", iAPPurchaseReturnID);
            return dal.GetDataSet(str);
        }
	}
