	public class PPConsolidateCostItemsController:BaseBusinessController
	{
		public PPConsolidateCostItemsController()
		{
			dal= new DALBaseProvider("PPConsolidateCostItems",typeof(PPConsolidateCostItemsInfo));
		}

        public double GetTotAmtByWOAndConsolidateCost(int WOIDs,int ConsolidateCostID)
        {
            double result = 0.0;
            string strQuery = String.Format("Select PPConsolidateCostItemAmt from PPConsolidateCostItems where FK_WOID = {0} and FK_PPConsolidateCostID = {1}", WOIDs, ConsolidateCostID);
            DataSet ds = dal.GetDataSet(strQuery);

            if (ds.Tables.Count > 0 && ds.Tables[0].Rows[0][0] != null && ds.Tables[0].Rows[0][0].ToString().Length > 0)
            {
                result = (double)ds.Tables[0].Rows[0][0];
            }
            return result;
        }
	}
